class Countdown{constructor(e){this.element=document.querySelector(e),this.countSecond=0,this.interval=setInterval(this.draw.bind(this),1e3)}addZero(e){return e<=9?"0"+e:e}draw(){this.countSecond++;let e=new Date,t=new Date;t.setDate(10),t.setHours(18,0,0);let n=t.getDate()-e.getDate(),o=t.getTime()-e.getTime(),a=this.addZero(Math.floor(o%864e5/36e5)),s=this.addZero(Math.floor(o%36e5/6e4)),d=this.addZero(Math.floor(o%6e4/1e3));switch(n){case 0:this.element.textContent="Nog "+a+":"+s+":"+d;break;case 1:this.element.textContent="Nog 1 dag en "+a+":"+s+":"+d;break;default:this.element.textContent="Nog "+n+" dagen"}this.countSecond>=30&&clearInterval(this.interval)}}let clock=new Countdown(".countdown"),clock2=new Countdown("#countdown2");