class Countdown{constructor(e){this.identifier=e,this.element=document.querySelector(".countdown"),this.now=new Date,this.specificDay=new Date}getText(){this.specificDay.setDate(10),this.specificDay.setHours(18),this.specificDay.setMinutes(0),this.specificDay.setSeconds(0);let e=this.specificDay.getDate()-this.now.getDate(),t=this.specificDay.getTime()-this.now.getTime(),s=Math.floor(t%864e5/36e5);Math.floor(t%36e5/6e4);console.log(s);let i="";switch(e){case 0:case 1:break;default:i="Nog "+e+" dagen"}return i}showCountdown(){this.element.textContent=this.specificDay>=this.now?this.getText():"Def"}}let clock=new Countdown(".countdown");clock.showCountdown();