class Countdown{constructor(t){this.identifier=t,this.element=document.querySelector(".countdown"),this.now=new Date,this.specificDay=new Date}addZero(t){return t<=9?"0"+t:t}getText(){this.specificDay.setDate(10),this.specificDay.setHours(18,0,0);let t=this.specificDay.getDate()-this.now.getDate(),e=this.specificDay.getTime()-this.now.getTime(),i=this.addZero(Math.floor(e%864e5/36e5)),o=this.addZero(Math.floor(e%36e5/6e4)),s=this.addZero(Math.floor(e%6e4/1e3));switch(t){case 0:this.text="Nog "+i+":"+o+":"+s;break;case 1:this.text="Nog 1 dag en "+i+":"+o+":"+s;break;default:this.text="Nog "+t+" dagen"}return this.text}showCountdown(){this.element.textContent=this.specificDay>=this.now?this.getText():"Def"}}let clock=new Countdown(".countdown");clock.showCountdown();